import React, { useEffect, useState } from "react";

const Headless = () => {
  const [OTPlessSignin, setOTPlessSignin] = useState(null);

  useEffect(() => {
    // Dynamically load OTPless Headless SDK script
    const script = document.createElement("script");
    script.id = "otpless-sdk";
    script.src = "https://otpless.com/v2/headless.js";
    script.setAttribute("data-appid", "RM2I31PAOMTTAJ0UGFLP"); // Replace YOUR_APP_ID with your actual app ID
    script.onload = initializeSDK; // Call initializeSDK function when script is loaded
    document.head.appendChild(script);

    return () => {
      document.head.removeChild(script);
    };
  }, []);

  // Function to initialize OTPless SDK
  const initializeSDK = () => {
    // Define callback function to handle authentication responses
    window.otpless = (otplessUser) => {
      const emailMap = otplessUser.identities.find(
        (item) => item.identityType === "EMAIL"
      );
      const mobileMap = otplessUser.identities.find(
        (item) => item.identityType === "MOBILE"
      )?.identityValue;
      const token = otplessUser.token;
      const email = emailMap?.identityValue;
      const mobile = mobileMap?.identityValue;
      const name = emailMap?.name || mobileMap?.name;

      console.log(otplessUser);
      // Implement your custom logic here.
    };

    setOTPlessSignin(new window.OTPless(window.otpless));
  };

  // Function to initiate phone authentication
  const phoneAuth = async () => {
    if (!OTPlessSignin) return;

    const countryCode = document.getElementById("country-code-input").value;
    const phoneNumber = document.getElementById("mobile-input").value;

    const res = await OTPlessSignin.initiate({
      channel: "PHONE", // Specify channel type as SMS
      phone: phoneNumber,
      countryCode: countryCode,
      deliveryChannel: "SMS",
      authType: "OTP",
    });

    alert(JSON.stringify(res));
  };

  // Function to verify OTP
  const verifyOTP = async () => {
    if (!OTPlessSignin) return;

    const phno = document.getElementById("mobile-input").value;
    const otp = document.getElementById("otp-input").value;

    const res = await OTPlessSignin.verify({
      channel: "PHONE",
      phone: phno,
      otp: otp,
      countryCode: "+91",
    });

    alert(JSON.stringify(res));
  };

  // Function to initiate authentication with OAuth providers
  const oAuth = async (provider) => {
    if (!OTPlessSignin) return;

    const res = await OTPlessSignin.initiate({
      channel: "OAUTH",
      channelType: provider,
    });

    alert(JSON.stringify(res));
  };

  return (
    <>
      <div id="mobile-section">
        <input id="country-code-input" placeholder="Country Code" />
        <input id="mobile-input" placeholder="Enter mobile number" />
        <button onClick={phoneAuth}>Request OTP</button>
      </div>
      <div id="otp-section">
        <input id="otp-input" placeholder="Enter OTP" />
        <button onClick={verifyOTP}>Verify OTP</button>
      </div>
    </>
  );
};

export default Headless;
