import React, { useEffect, useState } from "react";

const Headless = () => {
  const [OTPlessSignin, setOTPlessSignin] = useState(null);
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");

  useEffect(() => {
    // Dynamically load OTPless Headless SDK script
    const script = document.createElement("script");
    script.id = "otpless-sdk";
    script.src = "https://otpless.com/v2/headless.js";
    script.setAttribute("data-appid", "YOUR_APP_ID"); // Replace YOUR_APP_ID with your actual app ID
    script.onload = initializeSDK; // Call initializeSDK function when script is loaded
    document.head.appendChild(script);

    return () => {
      // Clean up to avoid memory leaks
      document.head.removeChild(script);
    };
  }, []);

  // Function to initialize OTPless SDK
  const initializeSDK = () => {
    // Define callback function to handle authentication responses
    window.otpless = (otplessUser) => {
      console.log("Received OTPless user:", otplessUser);
    };

    // Initialize OTPlessSignin after SDK is loaded
    setOTPlessSignin(new window.OTPless(window.otpless));
  };

  // Function to initiate phone authentication
  const requestOTP = async () => {
    if (!OTPlessSignin) return;

    const res = await OTPlessSignin.initiate({
      channel: "PHONE",
      phone: phone,
      countryCode: "+91",
    });

    console.log("Request OTP response:", res);
  };

  // Function to verify OTP
  const verifyOTP = async () => {
    if (!OTPlessSignin) return;

    const res = await OTPlessSignin.verify({
      channel: "PHONE",
      phone: phone,
      otp: otp,
      countryCode: "+91",
    });

    console.log("Verify OTP response:", res);
  };

  return (
    <>
      <div id="mobile-section">
        <input
          id="mobile-input"
          placeholder="Enter mobile number"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
        />
        <button onClick={requestOTP}>Request OTP</button>
      </div>
      <div id="otp-section">
        <input
          id="otp-input"
          placeholder="Enter OTP"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
        />
        <button onClick={verifyOTP}>Verify OTP</button>
      </div>
    </>
  );
};

export default Headless;
