import React, { useState } from "react";

const EmailAuth = ({ OTPlessSignin }) => {
  const [email, setEmail] = useState("");
  const [otp, setOtp] = useState("");
  const [response, setResponse] = useState(null);

  const handleEmailAuth = async () => {
    const res = await OTPlessSignin.initiate({
      channel: "EMAIL",
      email: email,
    });
    console.log("Response from email authentication:", res);
    setResponse(res);
    document.getElementById("otp-section").style.display = "block";
  };

  const handleVerifyOTP = async () => {
    const res = await OTPlessSignin.verify({
      channel: "EMAIL",
      email: email,
      otp: otp,
    });
    console.log("Response from OTP verification:", res);
    setResponse(res);
  };

  const copyResponse = () => {
    if (response) {
      navigator.clipboard
        .writeText(JSON.stringify(response, null, 2))
        .then(() => alert("Response copied to clipboard!"))
        .catch((error) => console.error("Failed to copy response:", error));
    }
  };

  return (
    <div>
      <div id="email-section">
        <input
          id="email-input"
          type="email"
          placeholder="Enter email address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
        />
        <button onClick={handleEmailAuth}>Request OTP</button>
      </div>

      <div id="otp-section" style={{ display: "none" }}>
        <input
          id="otp-input"
          type="text"
          placeholder="Enter OTP"
          value={otp}
          onChange={(e) => setOtp(e.target.value)}
        />
        <button onClick={handleVerifyOTP}>Verify OTP</button>
      </div>

      <div id="response-container">
        {response && (
          <>
            <h3>Response:</h3>
            <pre>{JSON.stringify(response, null, 2)}</pre>
            <button onClick={copyResponse}>Copy Response</button>
          </>
        )}
      </div>
    </div>
  );
};

export default EmailAuth;
